import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress,
  Alert
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const AsignacionForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [empleados, setEmpleados] = useState([]);
  const [fincas, setFincas] = useState([]);
  const [formData, setFormData] = useState({
    empleado_id: '',
    finca_id: '',
    fecha_asignacion: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    fetchEmpleados();
    fetchFincas();
    if (id) {
      fetchAsignacion();
    }
  }, [id]);

  const fetchEmpleados = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: 'http://127.0.0.1:5000/api/empleados',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmpleados(response.data);
    } catch (error) {
      console.error('Error al cargar empleados:', error);
      setError('Error al cargar la lista de empleados');
    }
  };

  const fetchFincas = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: 'http://127.0.0.1:5000/api/fincas',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setFincas(response.data);
    } catch (error) {
      console.error('Error al cargar fincas:', error);
      setError('Error al cargar la lista de fincas');
    }
  };

  const fetchAsignacion = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: `http://127.0.0.1:5000/api/asignaciones/${id}`,
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const asignacion = response.data;
      setFormData({
        empleado_id: asignacion.empleado_id,
        finca_id: asignacion.finca_id,
        fecha_asignacion: new Date(asignacion.fecha_asignacion).toISOString().split('T')[0]
      });
    } catch (error) {
      console.error('Error al cargar la asignación:', error);
      setError('Error al cargar los datos de la asignación');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      const method = id ? 'put' : 'post';
      const url = id 
        ? `http://127.0.0.1:5000/api/asignaciones/${id}`
        : 'http://127.0.0.1:5000/api/asignaciones';

      await axios({
        method,
        url,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        data: formData
      });

      navigate('/asignaciones');
    } catch (error) {
      console.error('Error al guardar la asignación:', error);
      setError('Error al guardar la asignación');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="sm">
      <Paper sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          {id ? 'Editar Asignación' : 'Nueva Asignación'}
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <form onSubmit={handleSubmit}>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="empleado-label">Empleado</InputLabel>
            <Select
              labelId="empleado-label"
              name="empleado_id"
              value={formData.empleado_id}
              onChange={handleChange}
              label="Empleado"
              required
            >
              {empleados.map(empleado => (
                <MenuItem key={empleado.id} value={empleado.id}>
                  {empleado.nombre} ({empleado.dpi})
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="finca-label">Finca</InputLabel>
            <Select
              labelId="finca-label"
              name="finca_id"
              value={formData.finca_id}
              onChange={handleChange}
              label="Finca"
              required
            >
              {fincas.map(finca => (
                <MenuItem key={finca.id} value={finca.id}>
                  {finca.nombre} - {finca.ubicacion}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            fullWidth
            label="Fecha de Asignación"
            type="date"
            name="fecha_asignacion"
            value={formData.fecha_asignacion}
            onChange={handleChange}
            sx={{ mb: 2 }}
            InputLabelProps={{
              shrink: true,
            }}
            required
          />

          <Box sx={{ mt: 2, display: 'flex', gap: 2 }}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              disabled={loading}
            >
              {id ? 'Actualizar' : 'Crear'}
            </Button>
            <Button
              variant="outlined"
              onClick={() => navigate('/asignaciones')}
            >
              Cancelar
            </Button>
          </Box>
        </form>
      </Paper>
    </Container>
  );
};

export default AsignacionForm;
