import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
  CircularProgress
} from '@mui/material';

const AsignacionForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEditing = Boolean(id);

  const [formData, setFormData] = useState({
    empleado_id: '',
    finca_id: '',
    fecha_asignacion: new Date().toISOString().split('T')[0]
  });

  const [empleados, setEmpleados] = useState([]);
  const [fincas, setFincas] = useState([]);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [initialLoading, setInitialLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all([
          fetchEmpleados(),
          fetchFincas(),
          isEditing ? fetchAsignacion() : Promise.resolve()
        ]);
      } finally {
        setInitialLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const fetchEmpleados = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://127.0.0.1:5000/api/empleados', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Error al cargar empleados');
      }

      const data = await response.json();
      setEmpleados(data);
    } catch (error) {
      console.error('Error:', error);
      setErrors(prev => ({ ...prev, empleados: 'Error al cargar la lista de empleados' }));
    }
  };

  const fetchFincas = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://127.0.0.1:5000/api/fincas', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Error al cargar fincas');
      }

      const data = await response.json();
      setFincas(data);
    } catch (error) {
      console.error('Error:', error);
      setErrors(prev => ({ ...prev, fincas: 'Error al cargar la lista de fincas' }));
    }
  };

  const fetchAsignacion = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://127.0.0.1:5000/api/asignaciones/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Error al cargar la asignación');
      }

      const data = await response.json();
      setFormData({
        empleado_id: data.empleado_id,
        finca_id: data.finca_id,
        fecha_asignacion: data.fecha_asignacion.split('T')[0]
      });
    } catch (error) {
      console.error('Error:', error);
      navigate('/asignaciones');
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.empleado_id) newErrors.empleado_id = 'Seleccione un empleado';
    if (!formData.finca_id) newErrors.finca_id = 'Seleccione una finca';
    if (!formData.fecha_asignacion) newErrors.fecha_asignacion = 'Seleccione una fecha';

    setErrors(prev => ({ ...prev, ...newErrors }));
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const url = isEditing 
        ? `http://127.0.0.1:5000/api/asignaciones/${id}`
        : 'http://127.0.0.1:5000/api/asignaciones';

      const response = await fetch(url, {
        method: isEditing ? 'PUT' : 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error('Error al guardar la asignación');
      }

      navigate('/asignaciones');
    } catch (error) {
      console.error('Error:', error);
      setErrors(prev => ({ ...prev, submit: 'Error al guardar la asignación' }));
    } finally {
      setLoading(false);
    }
  };

  if (initialLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
        <Typography variant="h5" gutterBottom>
          {isEditing ? 'Editar Asignación' : 'Nueva Asignación'}
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <FormControl fullWidth error={Boolean(errors.empleado_id)} disabled={loading}>
                <InputLabel>Empleado</InputLabel>
                <Select
                  value={formData.empleado_id}
                  onChange={(e) => setFormData({ ...formData, empleado_id: e.target.value })}
                  label="Empleado"
                >
                  {empleados.map((empleado) => (
                    <MenuItem key={empleado.id} value={empleado.id}>
                      {empleado.nombre} ({empleado.dpi})
                    </MenuItem>
                  ))}
                </Select>
                {errors.empleado_id && (
                  <FormHelperText>{errors.empleado_id}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <FormControl fullWidth error={Boolean(errors.finca_id)} disabled={loading}>
                <InputLabel>Finca</InputLabel>
                <Select
                  value={formData.finca_id}
                  onChange={(e) => setFormData({ ...formData, finca_id: e.target.value })}
                  label="Finca"
                >
                  {fincas.map((finca) => (
                    <MenuItem key={finca.id} value={finca.id}>
                      {finca.nombre} - {finca.ubicacion}
                    </MenuItem>
                  ))}
                </Select>
                {errors.finca_id && (
                  <FormHelperText>{errors.finca_id}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Fecha de Asignación"
                type="date"
                value={formData.fecha_asignacion}
                onChange={(e) => setFormData({ ...formData, fecha_asignacion: e.target.value })}
                error={Boolean(errors.fecha_asignacion)}
                helperText={errors.fecha_asignacion}
                InputLabelProps={{
                  shrink: true,
                }}
                disabled={loading}
              />
            </Grid>

            {errors.submit && (
              <Grid item xs={12}>
                <Typography color="error">{errors.submit}</Typography>
              </Grid>
            )}

            <Grid item xs={12}>
              <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                <Button
                  variant="outlined"
                  onClick={() => navigate('/asignaciones')}
                  disabled={loading}
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  disabled={loading}
                >
                  {loading ? 'Guardando...' : isEditing ? 'Actualizar' : 'Crear'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default AsignacionForm;
