import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  TablePagination,
  Box,
  CircularProgress,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';

const PagoList = () => {
  const [pagos, setPagos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [openDialog, setOpenDialog] = useState(false);
  const [pagoToDelete, setPagoToDelete] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPagos();
  }, []);

  const fetchPagos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: 'http://127.0.0.1:5000/api/pagos',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      // Convertir los valores numéricos
      const pagosFormateados = response.data.map(pago => ({
        ...pago,
        libras: parseFloat(pago.libras || 0),
        precio_libra: parseFloat(pago.precio_libra || 0),
        total: parseFloat(pago.total || 0)
      }));
      setPagos(pagosFormateados);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar los pagos:', error);
      setError('Error al cargar la lista de pagos');
      setLoading(false);
    }
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleEdit = (id) => {
    navigate(`/pagos/editar/${id}`);
  };

  const handleDeleteClick = (pago) => {
    setPagoToDelete(pago);
    setOpenDialog(true);
  };

  const handleDeleteConfirm = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios({
        method: 'delete',
        url: `http://127.0.0.1:5000/api/pagos/${pagoToDelete.id}`,
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPagos(pagos.filter(p => p.id !== pagoToDelete.id));
      setOpenDialog(false);
      setPagoToDelete(null);
    } catch (error) {
      console.error('Error al eliminar el pago:', error);
      setError('Error al eliminar el pago');
    }
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setPagoToDelete(null);
  };

  const formatNumber = (number) => {
    return isNaN(number) ? '0.00' : number.toFixed(2);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      </Container>
    );
  }

  return (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
          Lista de Pagos
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={() => navigate('/pagos/nuevo')}
          sx={{ mt: 4, mb: 2 }}
        >
          Nuevo Pago
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Empleado (DPI)</TableCell>
              <TableCell>Libras</TableCell>
              <TableCell>Precio/Libra</TableCell>
              <TableCell>Total</TableCell>
              <TableCell>Fecha de Pago</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {pagos
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((pago) => (
                <TableRow key={pago.id}>
                  <TableCell>{pago.id}</TableCell>
                  <TableCell>
                    {pago.empleado ? 
                      `${pago.empleado.nombre} (${pago.empleado.dpi})` : 
                      'N/A'}
                  </TableCell>
                  <TableCell>{formatNumber(pago.libras)}</TableCell>
                  <TableCell>Q{formatNumber(pago.precio_libra)}</TableCell>
                  <TableCell>Q{formatNumber(pago.total)}</TableCell>
                  <TableCell>
                    {new Date(pago.fecha_pago).toLocaleDateString()}
                  </TableCell>
                  <TableCell align="center">
                    <IconButton 
                      color="primary" 
                      onClick={() => handleEdit(pago.id)}
                      title="Editar"
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton 
                      color="error" 
                      onClick={() => handleDeleteClick(pago)}
                      title="Eliminar"
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={pagos.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Filas por página"
        />
      </TableContainer>

      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
      >
        <DialogTitle>Confirmar Eliminación</DialogTitle>
        <DialogContent>
          <DialogContentText>
            ¿Está seguro que desea eliminar el pago seleccionado?
            Esta acción no se puede deshacer.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Cancelar
          </Button>
          <Button onClick={handleDeleteConfirm} color="error" autoFocus>
            Eliminar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default PagoList;
