import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { es } from 'date-fns/locale';

const AsignacionForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEditing = Boolean(id);

  const [formData, setFormData] = useState({
    empleado_id: '',
    finca_id: '',
    fecha_asignacion: null,
  });

  const [empleados, setEmpleados] = useState([]);
  const [fincas, setFincas] = useState([]);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchEmpleados();
    fetchFincas();
    if (isEditing) {
      fetchAsignacion();
    }
  }, [id]);

  const fetchEmpleados = async () => {
    try {
      const response = await fetch('http://localhost:5000/empleados');
      if (!response.ok) throw new Error('Error al cargar empleados');
      const data = await response.json();
      setEmpleados(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const fetchFincas = async () => {
    try {
      const response = await fetch('http://localhost:5000/fincas');
      if (!response.ok) throw new Error('Error al cargar fincas');
      const data = await response.json();
      setFincas(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const fetchAsignacion = async () => {
    try {
      const response = await fetch(`http://localhost:5000/asignaciones/${id}`);
      if (!response.ok) throw new Error('Error al cargar la asignación');
      const data = await response.json();
      setFormData({
        empleado_id: data.empleado_id,
        finca_id: data.finca_id,
        fecha_asignacion: new Date(data.fecha_asignacion),
      });
    } catch (error) {
      console.error('Error:', error);
      navigate('/asignaciones');
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.empleado_id) newErrors.empleado_id = 'Seleccione un empleado';
    if (!formData.finca_id) newErrors.finca_id = 'Seleccione una finca';
    if (!formData.fecha_asignacion) newErrors.fecha_asignacion = 'Seleccione una fecha';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const url = isEditing 
        ? `http://localhost:5000/asignaciones/${id}`
        : 'http://localhost:5000/asignaciones';

      const method = isEditing ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          fecha_asignacion: formData.fecha_asignacion.toISOString().split('T')[0],
        }),
      });

      if (!response.ok) throw new Error('Error al guardar la asignación');

      navigate('/asignaciones');
    } catch (error) {
      console.error('Error:', error);
      setErrors({ submit: 'Error al guardar la asignación' });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    navigate('/asignaciones');
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
        <Typography variant="h5" gutterBottom>
          {isEditing ? 'Editar Asignación' : 'Nueva Asignación'}
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <FormControl fullWidth error={Boolean(errors.empleado_id)}>
                <InputLabel>Empleado</InputLabel>
                <Select
                  value={formData.empleado_id}
                  onChange={(e) => setFormData({ ...formData, empleado_id: e.target.value })}
                  label="Empleado"
                >
                  {empleados.map((empleado) => (
                    <MenuItem key={empleado.id} value={empleado.id}>
                      {empleado.nombre} ({empleado.dpi})
                    </MenuItem>
                  ))}
                </Select>
                {errors.empleado_id && (
                  <FormHelperText>{errors.empleado_id}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <FormControl fullWidth error={Boolean(errors.finca_id)}>
                <InputLabel>Finca</InputLabel>
                <Select
                  value={formData.finca_id}
                  onChange={(e) => setFormData({ ...formData, finca_id: e.target.value })}
                  label="Finca"
                >
                  {fincas.map((finca) => (
                    <MenuItem key={finca.id} value={finca.id}>
                      {finca.nombre} - {finca.ubicacion}
                    </MenuItem>
                  ))}
                </Select>
                {errors.finca_id && (
                  <FormHelperText>{errors.finca_id}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <LocalizationProvider dateAdapter={AdapterDateFns} adapterLocale={es}>
                <DatePicker
                  label="Fecha de Asignación"
                  value={formData.fecha_asignacion}
                  onChange={(newValue) => {
                    setFormData({ ...formData, fecha_asignacion: newValue });
                  }}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      fullWidth
                      error={Boolean(errors.fecha_asignacion)}
                      helperText={errors.fecha_asignacion}
                    />
                  )}
                />
              </LocalizationProvider>
            </Grid>

            {errors.submit && (
              <Grid item xs={12}>
                <Typography color="error">{errors.submit}</Typography>
              </Grid>
            )}

            <Grid item xs={12}>
              <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                <Button
                  variant="outlined"
                  onClick={handleCancel}
                  disabled={loading}
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  disabled={loading}
                >
                  {loading ? 'Guardando...' : 'Guardar'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default AsignacionForm;
