import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  TablePagination,
  Box,
  CircularProgress,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';

const AsignacionList = () => {
  const [asignaciones, setAsignaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [openDialog, setOpenDialog] = useState(false);
  const [asignacionToDelete, setAsignacionToDelete] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchAsignaciones();
  }, []);

  const fetchAsignaciones = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: 'http://127.0.0.1:5000/api/asignaciones',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      setAsignaciones(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar las asignaciones:', error);
      setError('Error al cargar la lista de asignaciones');
      setLoading(false);
    }
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleEdit = (id) => {
    console.log('Editando asignación:', id); // Para debugging
    navigate(`/asignaciones/editar/${id}`);
  };

  const handleDeleteClick = (asignacion) => {
    console.log('Intentando eliminar asignación:', asignacion); // Para debugging
    setAsignacionToDelete(asignacion);
    setOpenDialog(true);
  };

  const handleDeleteConfirm = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios({
        method: 'delete',
        url: `http://127.0.0.1:5000/api/asignaciones/${asignacionToDelete.id}`,
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setAsignaciones(asignaciones.filter(a => a.id !== asignacionToDelete.id));
      setOpenDialog(false);
      setAsignacionToDelete(null);
    } catch (error) {
      console.error('Error al eliminar la asignación:', error);
      setError('Error al eliminar la asignación');
    }
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setAsignacionToDelete(null);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      </Container>
    );
  }

  return (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
          Lista de Asignaciones
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={() => navigate('/asignaciones/nuevo')}
          sx={{ mt: 4, mb: 2 }}
        >
          Nueva Asignación
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Empleado</TableCell>
              <TableCell>Finca</TableCell>
              <TableCell>Fecha de Asignación</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {asignaciones
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((asignacion) => (
                <TableRow key={asignacion.id}>
                  <TableCell>{asignacion.id}</TableCell>
                  <TableCell>
                    {asignacion.empleado ? 
                      `${asignacion.empleado.nombre} (${asignacion.empleado.dpi})` : 
                      'N/A'}
                  </TableCell>
                  <TableCell>
                    {asignacion.finca ? 
                      `${asignacion.finca.nombre} - ${asignacion.finca.ubicacion}` : 
                      'N/A'}
                  </TableCell>
                  <TableCell>
                    {new Date(asignacion.fecha_asignacion).toLocaleDateString()}
                  </TableCell>
                  <TableCell align="center">
                    <IconButton 
                      color="primary" 
                      onClick={() => handleEdit(asignacion.id)}
                      title="Editar"
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton 
                      color="error" 
                      onClick={() => handleDeleteClick(asignacion)}
                      title="Eliminar"
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={asignaciones.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Filas por página"
        />
      </TableContainer>

      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
      >
        <DialogTitle>Confirmar Eliminación</DialogTitle>
        <DialogContent>
          <DialogContentText>
            ¿Está seguro que desea eliminar la asignación del empleado {asignacionToDelete?.empleado?.nombre} 
            en la finca {asignacionToDelete?.finca?.nombre}?
            Esta acción no se puede deshacer.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Cancelar
          </Button>
          <Button onClick={handleDeleteConfirm} color="error" autoFocus>
            Eliminar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AsignacionList;
