import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  TablePagination,
  Box,
  CircularProgress,
  Button,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TextField,
  MenuItem
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const AsignacionList = () => {
  const [asignaciones, setAsignaciones] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [fincas, setFincas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const navigate = useNavigate();

  // Estados para el diálogo de confirmación de eliminación
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [asignacionToDelete, setAsignacionToDelete] = useState(null);

  // Estados para el diálogo de edición
  const [openEditDialog, setOpenEditDialog] = useState(false);
  const [editingAsignacion, setEditingAsignacion] = useState({
    id: '',
    empleado_id: '',
    finca_id: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'Authorization': `Bearer ${token}`
      };

      const [asignacionesRes, empleadosRes, fincasRes] = await Promise.all([
        axios.get('http://127.0.0.1:5000/api/asignaciones', { headers }),
        axios.get('http://127.0.0.1:5000/api/empleados', { headers }),
        axios.get('http://127.0.0.1:5000/api/fincas', { headers })
      ]);

      setAsignaciones(asignacionesRes.data);
      setEmpleados(empleadosRes.data);
      setFincas(fincasRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar los datos:', error);
      setError('Error al cargar la información');
      setLoading(false);
    }
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Funciones para el manejo de la eliminación
  const handleDeleteClick = (asignacion) => {
    setAsignacionToDelete(asignacion);
    setOpenDeleteDialog(true);
  };

  const handleDeleteConfirm = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios({
        method: 'delete',
        url: `http://127.0.0.1:5000/api/asignaciones/${asignacionToDelete.id}`,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      setOpenDeleteDialog(false);
      fetchData();
    } catch (error) {
      console.error('Error al eliminar asignación:', error);
      setError('Error al eliminar la asignación');
    }
  };

  // Funciones para el manejo de la edición
  const handleEditClick = (asignacion) => {
    setEditingAsignacion({
      id: asignacion.id,
      empleado_id: asignacion.empleado_id,
      finca_id: asignacion.finca_id
    });
    setOpenEditDialog(true);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingAsignacion(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEditSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios({
        method: 'put',
        url: `http://127.0.0.1:5000/api/asignaciones/${editingAsignacion.id}`,
        data: editingAsignacion,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      setOpenEditDialog(false);
      fetchData();
    } catch (error) {
      console.error('Error al actualizar asignación:', error);
      setError('Error al actualizar la asignación');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      </Container>
    );
  }

  return (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
          Lista de Asignaciones
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={() => navigate('/asignaciones/nueva')}
          sx={{ mt: 4, mb: 2 }}
        >
          Nueva Asignación
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Empleado</TableCell>
              <TableCell>Finca</TableCell>
              <TableCell>Fecha de Asignación</TableCell>
              <TableCell>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {asignaciones
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((asignacion) => (
                <TableRow key={asignacion.id}>
                  <TableCell>{asignacion.id}</TableCell>
                  <TableCell>
                    {asignacion.empleado ? 
                      `${asignacion.empleado.nombre} (${asignacion.empleado.dpi})` : 
                      'N/A'}
                  </TableCell>
                  <TableCell>
                    {asignacion.finca ? 
                      `${asignacion.finca.nombre} - ${asignacion.finca.ubicacion}` : 
                      'N/A'}
                  </TableCell>
                  <TableCell>
                    {new Date(asignacion.fecha_asignacion).toLocaleDateString()}
                  </TableCell>
                  <TableCell>
                    <IconButton 
                      color="primary" 
                      onClick={() => handleEditClick(asignacion)}
                      size="small"
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton 
                      color="error" 
                      onClick={() => handleDeleteClick(asignacion)}
                      size="small"
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={asignaciones.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Filas por página"
        />
      </TableContainer>

      {/* Diálogo de confirmación de eliminación */}
      <Dialog
        open={openDeleteDialog}
        onClose={() => setOpenDeleteDialog(false)}
      >
        <DialogTitle>Confirmar Eliminación</DialogTitle>
        <DialogContent>
          <DialogContentText>
            ¿Está seguro que desea eliminar esta asignación?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Cancelar</Button>
          <Button onClick={handleDeleteConfirm} color="error">
            Eliminar
          </Button>
        </DialogActions>
      </Dialog>

      {/* Diálogo de edición */}
      <Dialog
        open={openEditDialog}
        onClose={() => setOpenEditDialog(false)}
      >
        <DialogTitle>Editar Asignación</DialogTitle>
        <DialogContent>
          <TextField
            select
            margin="normal"
            required
            fullWidth
            id="empleado_id"
            label="Empleado"
            name="empleado_id"
            value={editingAsignacion.empleado_id}
            onChange={handleEditChange}
          >
            {empleados.map((empleado) => (
              <MenuItem key={empleado.id} value={empleado.id}>
                {`${empleado.nombre} (${empleado.cedula})`}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            select
            margin="normal"
            required
            fullWidth
            id="finca_id"
            label="Finca"
            name="finca_id"
            value={editingAsignacion.finca_id}
            onChange={handleEditChange}
          >
            {fincas.map((finca) => (
              <MenuItem key={finca.id} value={finca.id}>
                {`${finca.nombre} - ${finca.ubicacion}`}
              </MenuItem>
            ))}
          </TextField>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenEditDialog(false)}>Cancelar</Button>
          <Button onClick={handleEditSubmit} color="primary">
            Guardar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AsignacionList;
