import React, { useState, useEffect } from 'react';
import { Box, Button, TextField, Typography, Container, Paper, MenuItem } from '@mui/material';
import axios from 'axios';
import { useReactToPrint } from 'react-to-print';

const PagoForm = () => {
  const [formData, setFormData] = useState({
    empleado_id: '',
    libras: '',
    precio_libra: ''
  });
  const [empleados, setEmpleados] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [pagoRealizado, setPagoRealizado] = useState(null);
  const componentRef = React.useRef();

  useEffect(() => {
    const fetchEmpleados = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/empleados', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setEmpleados(response.data);
      } catch (error) {
        setError('Error al cargar los empleados');
      }
    };
    fetchEmpleados();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/pagos', formData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setPagoRealizado(response.data);
      setSuccess('Pago registrado exitosamente');
      setFormData({ empleado_id: '', libras: '', precio_libra: '' });
      setError('');
    } catch (error) {
      setError(error.response?.data?.message || 'Error al registrar pago');
      setSuccess('');
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <Paper elevation={3} sx={{ p: 4, mt: 8 }}>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Typography component="h1" variant="h5">
            Registro de Pago
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
            <TextField
              select
              margin="normal"
              required
              fullWidth
              id="empleado_id"
              label="Empleado"
              name="empleado_id"
              value={formData.empleado_id}
              onChange={handleChange}
            >
              {empleados.map((empleado) => (
                <MenuItem key={empleado.id} value={empleado.id}>
                  {empleado.nombre} - {empleado.cedula}
                </MenuItem>
              ))}
            </TextField>
            <TextField
              margin="normal"
              required
              fullWidth
              id="libras"
              label="Libras de CafÃ©"
              name="libras"
              type="number"
              value={formData.libras}
              onChange={handleChange}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="precio_libra"
              label="Precio por Libra"
              name="precio_libra"
              type="number"
              value={formData.precio_libra}
              onChange={handleChange}
            />
            {error && (
              <Typography color="error" sx={{ mt: 2 }}>
                {error}
              </Typography>
            )}
            {success && (
              <Typography color="success" sx={{ mt: 2 }}>
                {success}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Registrar Pago
            </Button>
          </Box>
        </Box>
      </Paper>

      {pagoRealizado && (
        <Paper elevation={3} sx={{ p: 4, mt: 4 }} ref={componentRef}>
          <Box sx={{ textAlign: 'center' }}>
            <Typography variant="h5" gutterBottom>
              Recibo de Pago
            </Typography>
            <Typography variant="body1">
              Empleado: {empleados.find(e => e.id === parseInt(pagoRealizado.empleado_id))?.nombre}
            </Typography>
            <Typography variant="body1">
              Libras: {pagoRealizado.libras}
            </Typography>
            <Typography variant="body1">
              Precio por Libra: ${pagoRealizado.precio_libra}
            </Typography>
            <Typography variant="h6" sx={{ mt: 2 }}>
              Total: ${pagoRealizado.total}
            </Typography>
            <Typography variant="body2" sx={{ mt: 2 }}>
              Fecha: {new Date().toLocaleDateString()}
            </Typography>
          </Box>
          <Button
            fullWidth
            variant="contained"
            onClick={handlePrint}
            sx={{ mt: 2 }}
          >
            Imprimir Recibo
          </Button>
        </Paper>
      )}
    </Container>
  );
};

export default PagoForm;
