import React, { useState, useEffect } from 'react';
import { Box, Button, TextField, Typography, Container, Paper } from '@mui/material';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EmpleadoForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [formData, setFormData] = useState({
    nombre: '',
    cedula: '',
    telefono: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formErrors, setFormErrors] = useState({});

  useEffect(() => {
    if (id) {
      fetchEmpleado();
    }
  }, [id]);

  const fetchEmpleado = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios({
        method: 'get',
        url: `http://127.0.0.1:5000/api/empleados/${id}`,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      setFormData(response.data);
    } catch (error) {
      console.error('Error al cargar el empleado:', error);
      setError('Error al cargar los datos del empleado');
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.nombre) {
      errors.nombre = 'El nombre es requerido';
    }
    if (!formData.cedula) {
      errors.cedula = 'El DPI es requerido';
    }
    if (!formData.telefono) {
      errors.telefono = 'El teléfono es requerido';
    }
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!validateForm()) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const method = id ? 'put' : 'post';
      const url = id 
        ? `http://127.0.0.1:5000/api/empleados/${id}`
        : 'http://127.0.0.1:5000/api/empleados';

      const response = await axios({
        method,
        url,
        data: formData,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 200 || response.status === 201) {
        setSuccess(id ? 'Empleado actualizado exitosamente' : 'Empleado creado exitosamente');
        setTimeout(() => {
          navigate('/empleados');
        }, 2000);
      } else {
        setError('Error al procesar la solicitud');
      }
    } catch (error) {
      console.error('Error completo:', error);
      if (error.response?.data?.message) {
        setError(error.response.data.message);
      } else if (error.response?.status === 401) {
        setError('Sesión expirada. Por favor, inicie sesión nuevamente.');
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else if (error.request) {
        setError('No se pudo conectar con el servidor. Verifique su conexión.');
      } else {
        setError('Error al procesar la solicitud. Por favor, intente nuevamente.');
      }
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <Paper elevation={3} sx={{ p: 4, mt: 8 }}>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Typography component="h1" variant="h5">
            {id ? 'Editar Empleado' : 'Registrar Empleado'}
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="nombre"
              label="Nombre"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              error={!!formErrors.nombre}
              helperText={formErrors.nombre}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="cedula"
              label="DPI"
              name="cedula"
              value={formData.cedula}
              onChange={handleChange}
              error={!!formErrors.cedula}
              helperText={formErrors.cedula}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="telefono"
              label="Teléfono"
              name="telefono"
              value={formData.telefono}
              onChange={handleChange}
              error={!!formErrors.telefono}
              helperText={formErrors.telefono}
            />
            {error && (
              <Typography color="error" sx={{ mt: 2 }}>
                {error}
              </Typography>
            )}
            {success && (
              <Typography color="success.main" sx={{ mt: 2 }}>
                {success}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              {id ? 'Actualizar' : 'Registrar'}
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default EmpleadoForm;
